mapper.select:
SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REF DESC, STEP ASC)AS RNUM, SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT FROM BBS WHERE 1=1
AND TITLE LIKE '%'||#{searchWord}||'%'
AND CONTENT LIKE '%'||#{searchWord}||'%'
AND ID=#{searchWord}
) A WHERE RNUM BETWEEN ${start} AND ${end}

SELECT SEQ, ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, DEL, READCOUNT 
FROM BBS
WHERE SEQ=#{seq}

mapper.update:
UPDATE BBS
SET READCOUNT=READCOUNT+1
WHERE SEQ=#{seq}

UPDATE BBS
SET DEL=1
WHERE SEQ=#{seq}

UPDATE BBS SET
TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE
WHERE SEQ=#{seq}
